function HoneyHope(btn1, btn2, btn3, table, modal){
	var that = this;
	that.Route = ""; that.Types = ""; that.Datos = ""; that.page = ""; that.btn1 = btn1; that.btn2 = btn2; that.btn3 = btn3; that.tabla = table; that.Modall = modal;
	
	that.Modal = function(btn, value){
		that.Muestra(value);
		if(value != 1){
			$.ajax({
				url: that.Route,
				type: that.Types,
				dataType: 'json',
				success : function(response){
					that.LlenaModal(response);
				},
				error:  function(){
					that.getMsj_Error();	
				}
			});		
		}else{
			$('input[type = "text"]').val('');
			$('input[type = "email"]').val('');
			$('input[type = "select"]').val('');
			$('textarea').val('');
			$('number').val('');
			$(that.Modall).modal('show');
		}
	}

	that.Muestra = function(value){
		that.btn1 = (value == 1) ? that.btn1.show() : that.btn1.hide();
		that.btn2 = (value == 2) ? that.btn2.show() : that.btn2.hide();
		that.btn3 = (value == 3) ? that.btn3.show() : that.btn3.hide();
	}

	that.CargaTabla = function(value) {}

	that.CargaDatosTabla = function(pagination = 0, data = null) {}

	that.Ajax = function(Token){
		$.ajax({
			url: that.Route,
			headers: {'X-CSRF-TOKEN': Token },
			type:that.Types,
			dataType: 'json',
			data: that.Datos,
			success : function(response){
				$(response).each(function(){
					if(this.Bln_Bandera == 1){
						if($("#alert_cruds").hasClass('alert-danger')){
							$("#alert_cruds").removeClass('alert-danger');
							$("#alert_cruds").addClass('alert-success');			
						}
						$("#alert_cruds").fadeIn();
						$("#alert_cruds #txt_response").text(this.Vch_Mensaje);	
					}else{
						if($("#alert_cruds").hasClass('alert-success')){
							$("#alert_cruds").removeClass('alert-success');
							$("#alert_cruds").addClass('alert-danger');	
						}
						$("#alert_cruds").fadeIn();
						$("#alert_cruds #txt_response").text(this.Vch_Mensaje);
					}
					that.CargaTabla(2);
					$(that.Modall).modal('toggle');
				});
			},
			error : function(){
				that.getMsj_Error();
			}
		});
	}

	that.Pagination = function(){
		$.ajax({
			url: that.Route,
			data: { page : that.page },
			type: 'GET',
			dataType: 'json',
			success: function(data){
				that.CargaDatosTabla(that.page, data);	
			}
		});
	}

	that.LlenaModal = function(response){}

	that.getMsj_Error = function(){
		alert("Ocurrio un error en el servidor");
	}
}
