function HoneyPuros(btn1, btn2, btn3, table, modal, tablepage, Id, Text, Routing) {
	HoneyHope.apply(this, [btn1, btn2, btn3, table, modal]);
	var that = this, tablap = tablepage;

	that.CargaDatosTabla = function(pagination = 0, data = null){
		that.page = (pagination == 0)? that.page = 1 : that.page = pagination;
		that.tabla.empty();
		$.ajax({
			url: "/"+Routing,
			type: 'GET',
			data: {page : that.page},
			dataType: 'json',
			beforeSend: function(){
				$('.preloader').show();
			},
			success : function(response){
				$(response.data).each(function(key,value){
					that.tabla.append("<tr><td>"+$(this).attr(Id)+"</td>"+
						"<td>"+$(this).attr(Text)+"</td>"+
						"<td><button class='btn btn-success' id='btn_editpuro' OnClick='' value="+$(this).attr(Id)+">Editar</button></td>"+
						"<td><button class='btn btn-danger' id='btn_deltpuro' OnClick='' value="+$(this).attr(Id)+">Eliminar</button></td></tr>");
				});
				$(tablepage).append(that.tabla);
				if(data != null){ $("#render").html(data); }
				$('.preloader').hide(); 
			}
		});
	}
}

HoneyPuros.prototype = HoneyHope;
