$(document).ready(function(){
	var id = $("#id_name").prop('outerText');
	$.ajax({
		url: '/Admin/'+id.trim()+"/edit",
		type: 'GET',
		dataType: 'json',
		success : function(response){
			$(response).each(function(){
				if(this.ruta != null){ $("#picprofile").css({'background-image': 'url(../images/'+this.ruta+')' }); }
			});
		},
		error : function(){
			//url = '/';
			//Urls();	
		}
	});
});


