function HoneyApiarios() {
	HoneyHope.apply(this, [$("#btn_creapi"), $("#btn_updapi"), $("#btn_delapi"), $('<tbody></tbody>').attr('id', 'tbody_api'), $("#modal-api")]);
	var Clave = $("#clave_api"), Nombre = $("#nom_api"), Sucursal = $("#suc_api"), token = $("#token_api").val(), that = this;

	that.btn1.click(function(){
		that.Route = "/Apiarios";
		that.Types = "POST";
		that.Datos = { Sml_IdApi: Clave.val(), Vch_NombreApi: Nombre.val(), Sml_IdSucu : Sucursal.val(), Vch_Tipo : 'Insertar' };
		that.Ajax(token);
	});	

	that.btn2.click(function(){
		that.Route = "/Apiarios/"+Clave.val()+"";
		that.Types = "PUT";
		that.Datos = { Sml_IdApi: Clave.val(), Vch_NombreApi: Nombre.val(), Sml_IdSucu : Sucursal.val(), Vch_Tipo : 'Actualizar' };
		that.Ajax(token);
	});

	that.btn3.click(function(){
		that.Route = "/Apiarios/"+Clave.val()+"";
		that.Types = "DELETE";
		that.Datos = { Sml_IdApi: Clave.val(), Vch_NombreApi: Nombre.val(), Sml_IdSucu : Sucursal.val(), Vch_Tipo : 'Eliminar' };
		that.Ajax(token);
	});

	$("#btn_newapiario").click(function(){ that.Modal(this, 1); });

	$(document).on('click', '#btn_editapi', function(e){
		e.preventDefault();
		that.Route = "/Apiarios/"+this.value+"/edit";
		that.Types = "GET";
		that.Modal(this, 2);
	});

	$(document).on('click', '#btn_deleteapi', function(e){
		e.preventDefault();
		that.Route = "/Apiarios/"+this.value+"/edit";
		that.Types = "GET";
		that.Modal(this, 3);
	});

	$(document).on('click', '.pagination a', function(e){
		e.preventDefault();
		that.page = $(this).attr('href').split('page=')[1];
		that.Route = "/Apiarios";
		that.Pagination();
	});

	that.CargaTabla = function(value){
		that.Route = "/Apiarios";
		that.tabla.empty();
		$.get(that.Route, function(response){
			if(value == 1){ that.CargaDatosTabla(); }else{ that.CargaDatosTabla(1, response); }
		});
	}

	that.CargaDatosTabla = function(pagination = 0, data = null){
		that.Route = "/apitable";
		that.page = (pagination == 0) ? that.page = 1 : that.page = pagination;
		that.tabla.empty();
		$.ajax({
			url: that.Route,
			data: { page: that.page },
			type: 'GET',
			dataType: 'json',
			beforeSend: function(){
				$('.preloader').show();
			},
			success : function(response){
				$(response.data).each(function(){
					that.tabla.append("<tr><td>"+this.Sml_IdApiario+"</td>"+
						"<td>"+this.Vch_NombreApiario+"</td>"+
						"<td>"+this.Vch_Nombre+"</td>"+
						"<td><button class='btn btn-success' id='btn_editapi' value="+this.Sml_IdApiario+">Editar</button></td>"+
						"<td><button class='btn btn-danger' id='btn_deleteapi' value="+this.Sml_IdApiario+">Eliminar</button></td></tr>");
				});
				$("#tableapi").append(that.tabla);
				if(data != null){ $("#render").html(data); }
				$('.preloader').hide();
			}
		}); 	
	}

	that.CargaCombo = function(){
		$.ajax({
			url: '/apicombo',
			type: 'GET',
			dataType: 'json',
			success: function(response){
				$(response).each(function(){
					var option = $(document.createElement('option'));
					option.val(this.Sml_IdSucursal);
					option.text(this.Sucursal);
					Sucursal.append(option);
				});
			},
			error : function(){
				alert(that.getMsj_Error());
			}
		});
	}

	that.LlenaModal = function(response){
		$(response).each(function(){
			Clave.val(this.Sml_IdApiario); Nombre.val(this.Vch_NombreApiario);
			$('#suc_api option:contains('+this.Vch_Nombre+')').attr('selected', 'selected');
		});
		that.Modall.modal('show');
	}
}

HoneyApiarios.prototype = HoneyHope;
var ha = new HoneyApiarios();

$(document).ready(function(){
	ha.CargaTabla(0);
	ha.CargaCombo();
});