function HoneySize() {
	HoneyPuros.apply(this, [$("#btn_crecap"), $("#btn_upcap"), $("#btn_delcap"), $('<tbody></tbody>').attr('id', 'tbody_cap'), $("#modal-cap"), $("#tablecap"), 'Sml_IdCapacidad', 'Vch_Capacidad', 'captable' ]);		
	var Clave = $("#clv_cap"), Capacidad = $("#capaci"), token = $("#token_cap").val(), that = this;

	that.btn1.click(function(){
		that.Route = "/Capc/";
		that.Types = "POST";
		that.Datos = { Sml_idCapa : Clave.val(), Vch_Capac: Capacidad.val(), TipoCrud: 'Insertar' };
		that.Ajax(token);
	});

	that.btn2.click(function(){
		that.Route = "/Capc/"+Clave.val()+"";
		that.Types = "PUT";
		that.Datos = { Sml_idCapa : Clave.val(), Vch_Capac: Capacidad.val(), TipoCrud: 'Actualizar'};
		that.Ajax(token);
	});

	that.btn3.click(function(){
		that.Route = "/Capc/"+Clave.val()+"";
		that.Types = "DELETE";
		that.Datos = { Sml_idCapa : Clave.val(), Vch_Capac: Capacidad.val(), TipoCrud: 'Eliminar'};
		that.Ajax(token);
	});

	$(document).on('click', '#btn_editpuro', function(e){
		e.preventDefault();
		that.Route = "/Capc/"+this.value+"/edit";
		that.Types = "GET";
		that.Modal(this, 2);
	});

	$(document).on('click', '#btn_deltpuro', function(e){
		e.preventDefault();
		that.Route = "/Capc/"+this.value+"/edit";
		that.Types = "GET";
		that.Modal(this, 3);
	});

	$(document).on('click', '.pagination a', function(e){
		e.preventDefault();
		that.page = $(this).attr('href').split('page=')[1];
		that.Route = "/Capc";
		that.Pagination();
	});

	$("#btn_newcap").click(function(){ that.Modal(this, 1); });

	that.CargaTabla = function(value){
		that.tabla.empty();
		that.Route = "/Capc";
		$.get(that.Route, function(response){
			if(value == 1) { that.CargaDatosTabla(); }
			else{ that.CargaDatosTabla(1, response); }	
		});
	}

	that.LlenaModal = function(response){
		$(response).each(function(){
			Clave.val(this.Sml_IdCapacidad);
			Capacidad.val(this.Vch_Capacidad);
		});
		$(that.Modall).modal('show');
	}
}	 

HoneySize.prototype = HoneyPuros;

var hs = new HoneySize();

$(document).ready(function(){
	hs.Route = "/Capc";
	hs.CargaTabla(0);
});