function HoneyPosition(){
	HoneyPuros.apply(this, [$("#btn_crecargo"), $("#btn_upcargor"), $("#btn_delcargo"), $('<tbody></tbody>').attr('id', 'tbody_cargo'), $("#modal-cargo"), $("#tablecargo") , 'Sml_Cargo', 'Vch_Cargo', 'tablecargo' ]);
	var Clave = $("#clv_cargo"), Cargo = $("#txt_cargo"), token = $("#token_cargo").val(), that = this;

	that.btn1.click(function(){
		that.Route = "/Cargo/";
		that.Types = "POST";
		that.Datos = { Sml_Cargo : Clave.val(), Vch_Cargo : Cargo.val(), TipoCrud: 'Insertar' };
		that.Ajax(token);
	});

	that.btn2.click(function(){
		that.Route = "/Cargo/"+Clave.val()+"";
		that.Types = "PUT";
		that.Datos = { Sml_Cargo : Clave.val(), Vch_Cargo : Cargo.val(), TipoCrud: 'Actualizar' };
		that.Ajax(token);
	});

	that.btn3.click(function(){
		that.Route = "/Cargo/"+Clave.val()+"";
		that.Types = "DELETE";
		that.Datos = { Sml_Cargo : Clave.val(), Vch_Cargo : Cargo.val(), TipoCrud: 'Eliminar' };
		that.Ajax(token);
	});

	$(document).on('click', '.pagination a', function(e){
		e.preventDefault();
		that.page = $(this).attr('href').split('page=')[1];
		that.Route = "/Cargo";
		that.Pagination();
	});

	$("#btn_newcargo").click(function(){ that.Modal(this, 1) });

	$(document).on('click', '#btn_editpuro', function(e){
		e.preventDefault();
		that.Route = "/Cargo/"+this.value+"/edit";
		that.Types = "GET";
		that.Modal(this, 2);	
	});

	$(document).on('click', '#btn_deltpuro', function(e){
		e.preventDefault();
		that.Route = "/Cargo/"+this.value+"/edit";
		that.Types = "GET";
		that.Modal(this, 3);
	});

	that.CargaTabla = function(value){
		that.tabla.empty();
		that.Route = "/Cargo";
		$.get(that.Route, function(response){
			if(value == 1) { that.CargaDatosTabla(); }
			else{ that.CargaDatosTabla(1, response); }	
		});
	}

	that.LlenaModal = function(response){
		$(response).each(function(){
			Clave.val(this.Sml_Cargo);
			Cargo.val(this.Vch_Cargo);
		});
		$(that.Modall).modal('show');	
	}
}

HoneyPosition.prototype = HoneyPuros;
var hp = new HoneyPosition();

$(document).ready(function(){
	hp.Route = "/Cargo";
	hp.CargaTabla(0);
});



