function HoneyPurchases() {
	HoneyHope.apply(this, [$("#btn_crecompra"), $("#btn_upcompra"), $("#btn_delcompra"), $('<tbody></tbody>').attr('id', 'tbody_compra'), $("#modal-compra")]);
	var Clave = $("#clv_compra"), Articulo = $("#nom_art"), Cantidad = $("#cant_art"), Empresa = $("#emp"), Precio = $("#precio_art"), token = $("#token_compra").val(), that = this;

	that.btn1.click(function(){
		that.Route = "/Compras";
		that.Types = "POST";
		that.Datos = { Sml_IdArt: Clave.val(), Vch_NomArti : Articulo.val(), Int_cant : Cantidad.val(), Sml_IdEmp : Empresa.val(), Flt_prec : Precio.val(), Vch_Tipo: 'Insertar' };
		that.Ajax(token);
	});

	that.btn2.click(function(){
		that.Route = "/Compras/"+Clave.val()+"";
		that.Types = "PUT";
		that.Datos = { Sml_IdArt: Clave.val(), Vch_NomArti : Articulo.val(), Int_cant : Cantidad.val(), Sml_IdEmp : Empresa.val(), Flt_prec : Precio.val(), Vch_Tipo: 'Actualizar' };
		that.Ajax(token);
	});

	that.btn3.click(function(){
		that.Route = "/Compras/"+Clave.val()+"";
		that.Types = "DELETE";
		that.Datos = { Sml_IdArt: Clave.val(), Vch_NomArti : Articulo.val(), Int_cant : Cantidad.val(), Sml_IdEmp : Empresa.val(), Flt_prec : Precio.val(), Vch_Tipo: 'Eliminar' };
		that.Ajax(token);	
	});

	$("#btn_newcompra").click(function(){ that.Modal(this, 1); });

	$(document).on('click', '#btn_editcompra', function(e){
		e.preventDefault();
		that.Route = "/Compras/"+this.value+"/edit";
		that.Types = "GET";
		that.Modal(this, 2);	
	});

	$(document).on('click', '#btn_deletecompra', function(e){
		e.preventDefault();
		that.Route = "/Compras/"+this.value+"/edit";
		that.Types = "GET";
		that.Modal(this, 3);
	});

	that.CargaTabla = function(value){
		that.Route = "/Compras/";
		that.tabla.empty();
		$.get(that.Route, function(response){
			if(value == 1){ that.CargaDatosTabla(); }else{ that.CargaDatosTabla(1, response); }
		});
	}

	that.CargaDatosTabla = function(pagination = 0, data = null){
		that.Route = "/compratable";
		that.page = (pagination == 0)? that.page = 1 : that.page = pagination;
		that.tabla.empty();
		$.ajax({
			url: that.Route,
			data: { page: that.page },
			type: 'GET',
			dataType: 'json',
			beforeSend: function(){
				$('.preloader').show();
			},
			success : function(response){
				$(response.data).each(function(){
					that.tabla.append("<tr><td>"+this.Sml_IdArticulo+"</td>"+
						"<td>"+this.Vch_NomArticulo+"</td><td>"+this.Int_Cantidad+"</td><td>"+this.Vch_Nombre+"</td><td>"+this.Flt_Precio+"</td>"+
						"<td><button class='btn btn-success' id='btn_editcompra' value="+this.Sml_IdArticulo+">Editar</button></td>"+
						"<td><button class='btn btn-danger' id='btn_deletecompra' value="+this.Sml_IdArticulo+">Eliminar</button></td></tr>");	
				});
				$("#tablecompra").append(that.tabla);
				if(data != null){ $("#render").html(data); }
				$('.preloader').hide(); 
			}
		});
	}

	that.CargaCombo = function(){
		$.ajax({
			url: '/compracombo',
			type: 'GET',
			dataType: 'json',
			success : function(response){
				$(response).each(function(){
					var option = $(document.createElement('option'));
					option.val(this.Sml_IdEmpresa);
					option.text(this.Vch_Nombre);
					Empresa.append(option);	
				});
			},
			error : function(){
				alert(that.getMsj_Error());
			}
		});
	}

	that.LlenaModal = function(response){
		$(response).each(function(){
			Clave.val(this.Sml_IdArticulo); Articulo.val(this.Vch_NomArticulo); Cantidad.val(this.Int_Cantidad); Precio.val(this.Flt_Precio);
			$('#emp option:contains('+this.Vch_Nombre+')').attr('selected', 'selected');
		});
		that.Modall.modal('show');
	}
}

HoneyPurchases.prototype = HoneyHope;
var hp = new HoneyPurchases();

$(document).ready(function(){
	hp.CargaTabla(0);
	hp.CargaCombo();
});