function SectionMyV() {
	var that = this, cards;

	that.ConsumeDatos = function(){
		$.ajax({
			url: '/tablemyv',
			//url: '../public/tablemyv',
			type: 'GET',
			dataType: 'json',
			success : function(response){
				$(".row").empty();
				that.Iterator(response);
			},
			error : function(){
				alert("Ocurrio un error");
			}
		});
	}

	that.Iterator = function(response){
		$(response.data).each(function(){
			cards = $("<div class='col-md-6'><div class='panel-group material-accordion' id='accordion"+this.id+"'>"+
				"<div class='panel panel-default material-accordion__panel'>"+
				"<div class='panel-heading material-accordion__heading' id='acc_headingOne'><h4 class='panel-title'>"+
				"<a data-toggle='collapse' data-parent='#accordion"+this.id+"' href='#acc_collapse"+this.id+"' class='material-accordion__title'>"+this.titulo+"</a></h4></div>"+
				"<div id='acc_collapse"+this.id+"' class='panel-collapse collapse in material-accordion__collapse'>"+
				"<div class='panel-body'>"+this.descripcion+"</div></div>"+
				"</div></div></div");
			$(".row").append(cards);
		});
	}
}

var myv = new SectionMyV();

document.addEventListener("DOMContentLoaded",Evento, false)

function Evento(){
	var nav = $('.nav li').removeClass('active');
	$(nav[2]).addClass('active');
	myv.ConsumeDatos();
}