function HoneyHome(){
	var that = this, div = $("#containerrow"), card;

	that.ConsumeDatos = function(){
		$.ajax({
			url: '/tableinicio',
			//url: '../public/tableinicio',
			type: 'GET',
			datatype: 'json',
			success : function(response){
				that.Iterator(response);				
			},
			error : function(){
				alert("Ocurrio un eror en el servidor alerta");
			}
		});
	}

	that.Iterator = function(response){
		$(response.data).each(function(){
			card = $("<div class='col-md-4'>"+
				"<div class='thumbnail material-card'>"+
				"<header class='material-card__header'><img src='images/"+this.Vch_RutaImagen+"'></header>"+
				"<div class='material-card__content'><h3 class='material-card__title'>"+this.Vch_Titulo+"</h3><p>"+this.Vch_Descripcion+"</p></div>"+
				"<footer class='material-card__footer'><div class='fb-share-button' data-href='https://honeyhope.com/' data-layout='button_count' data-size='small' data-mobile-iframe='true'><a class='fb-xfbml-parse-ignore' target='_blank' href='https://www.facebook.com/sharer/sharer.php?u=https%3A%2F%2Fhoneyhope.com%2F&amp;src=sdkpreparse'>Compartir</a></div</footer>"+
				"</div></div>");
			$(".row").append(card);
		});
	}
}

var home = new HoneyHome();

$(document).ready(function(){
	var nav = $('.nav li').removeClass('active');
	$(nav[0]).addClass('active');
	home.ConsumeDatos();
});