function HoneyNSuc() {
	var that = this, card;

	that.ConsumeDatos = function(){
		$.ajax({
			url: '/tablensuc',
			type: 'GET',
			dataType: 'json',
			success : function(response){
				that.Iterator(response);	
			},
			error : function(){
				alert("Ocurrio un error en el servidor");
			}
		});
	}

	that.Iterator = function(response){
		$(".row").empty();
		$(response.data).each(function(){
			card = $("<div class='col-md-4'><div class='thumbnail material-card'>"+
				"<header class='material-card__header2'><img src='images/"+this.ruta+"' class='img-responsive'><h2>"+this.Sucursal+"</h2><p></p><h5><i class='glyphicon glyphicon-time'></i>&nbsp;"+this.horarios+"</h5></header>"+
				"<div class='material-card__footer2'><div class='form-group'><div class='row'>"+
				"<div class='col-md-6'><h5><i class='glyphicon glyphicon-map-marker'></i>&nbsp;"+this.Direccion+"</h5></div>"+
				"<div class='col-md-6'><h5><i class='glyphicon glyphicon-earphone'></i>&nbsp;Tel. "+this.Telefono+"</h5></div>"+
				"</div></div></div>"+
				"</div></div>");
			$("#rowcontainer").append(card);
		});
	}
}

var nsuc = new HoneyNSuc();

$(document).ready(function(){
	var nav = $('.nav li').removeClass('active');
	$(nav[3]).addClass('active');
	nsuc.ConsumeDatos();
});