function HoneyHon() {
	HoneyHope.apply(this, [$("#btn_cremiel"), $("#btn_updmiel"), $("#btn_delmiel"), $('<tbody></tbody>').attr('id', 'tbody_honey'), $("#modal-miel")]);
	var Clave = $("#clv_miel"), Apiario = $("#sec_api"), Cantidad = $("#cant_miel"), Fechaa = $("#fec_miel"), token = $("#token_miel").val(), that = this;

	
	$("#btn_newreg").click(function(){ that.Modal(this, 1); });

	that.btn1.click(function(){
		that.Route = "/Miel/";
		that.Types = "POST";
		that.Datos = { Sml_Reg : Clave.val(), Sml_IdApi : Apiario.val(), Flt_Cant : Cantidad.val(), Fecha : Fechaa.val(), Vch_Tipo : 'Insertar' };
		that.Ajax(token);
	});

	that.btn2.click(function(){
		that.Route = "/Miel/"+Clave.val()+"";
		that.Types = "PUT";
		that.Datos = { Sml_Reg : Clave.val(), Sml_IdApi : Apiario.val(), Flt_Cant : Cantidad.val(), Fecha : Fechaa.val(), Vch_Tipo : 'Actualizar' };
		that.Ajax(token);
	});	

	that.btn3.click(function(){
		that.Route = "/Miel/"+Clave.val()+"";
		that.Types = "DELETE";
		that.Datos = { Sml_Reg : Clave.val(), Sml_IdApi : Apiario.val(), Flt_Cant : Cantidad.val(), Fecha : Fechaa.val(), Vch_Tipo : 'Eliminar' };
		that.Ajax(token);
	});

	$(document).on('click', '#btn_edithoney', function(e){
		e.preventDefault();
		that.Route = "/Miel/"+this.value+"/edit";
		that.Types = "GET";
		that.Modal(this, 2);
	});

	$(document).on('click', '#btn_delethoney', function(e){
		e.preventDefault();
		that.Route = "/Miel/"+this.value+"/edit";
		that.Types = "GET";
		that.Modal(this, 3);
	});

	that.CargaTabla = function(value){
		that.Route = "/Miel/";
		that.tabla.empty();
		$.get(that.Route, function(response){
			if(value == 1){ that.CargaDatosTabla(); }else{ that.CargaDatosTabla(1, response); }
		});
	}

	that.CargaDatosTabla = function(pagination = 0, data = null){
		that.Route = "/mieltable";
		that.page = (pagination == 0)? that.page = 1 : that.page = pagination;
		that.tabla.empty();
		$.ajax({
			url: that.Route,
			data: { page: that.page },
			type: 'GET',
			dataType: 'json',
			beforeSend: function(){
				$('.preloader').show();
			},
			success : function(response){
				$(response.data).each(function(){
					that.tabla.append("<tr><td>"+this.Sml_Registro+"</td>"+
						"<td>"+this.Vch_NombreApiario+"</td><td>"+this.Flt_Cantidad+"</td><td>"+this.Dtm_Fecha+"</td>"+
						"<td><button class='btn btn-success' id='btn_edithoney' value="+this.Sml_Registro+">Editar</button></td>"+
						"<td><button class='btn btn-danger' id='btn_delethoney' value="+this.Sml_Registro+">Eliminar</button></td></tr>");
				});
				$("#tablehoney").append(that.tabla);
				if(data != null){ $("#render").html(data); }
				$('.preloader').hide(); 
			}
		});
	}

	that.CargaCombo = function(){
		$.ajax({
			url:'/mielcombo',
			type: 'GET',
			dataType: 'json',
			success : function(response){
				$(response).each(function(){
					var option = $(document.createElement('option'));
					option.val(this.Sml_IdApiario);
					option.text(this.Vch_NombreApiario);
					Apiario.append(option);
				});
			},
			error : function(){
				alert(that.getMsj_Error());
			}
		});
	}

	that.LlenaModal = function(response){
		$(response).each(function(){
			var date = this.Dtm_Fecha.split(' ');
			Clave.val(this.Sml_Registro); Cantidad.val(this.Flt_Cantidad); Fechaa.val(date[0]);
			$('#sec_api option:contains('+this.Vch_NombreApiario+')').attr('selected', 'selected');
		});
		that.Modall.modal('show');
	}

}

HoneyHon.prototype = HoneyHope;

var hh = new HoneyHon();

$(document).ready(function(){
	hh.CargaTabla(0);
	hh.CargaCombo();
});