function HoneyAppearance() {
	HoneyPuros.apply(this,[$("#btn_crepres"), $("#btn_uppres"), $("#btn_delpres"), $('<tbody></tbody>').attr('id', 'tbody_pres'), $("#modal-pres"), $("#tablepres"), 'Sml_IdPresentacion', 'Vch_Presentacion', "prestable"]);
	var Clave = $("#clv_pres"), Presentacion = $("#pres"), token = $("#token_pres").val(),  that = this;

	that.btn1.click(function(){
		that.Route = "/Presentacion/";
		that.Types = "POST";
		that.Datos = { Sml_IdPres: Clave.val(), Vch_Prese: Presentacion.val(), TipoCrud: 'Insertar' };
		that.Ajax(token);
	});

	that.btn2.click(function(){
		that.Route = "/Presentacion/"+Clave.val()+"";
		that.Types = "PUT";
		that.Datos = { Sml_IdPres: Clave.val(), Vch_Prese: Presentacion.val(), TipoCrud: 'Actualizar' };
		that.Ajax(token);
	});

	that.btn3.click(function(){
		that.Route = "/Presentacion/"+Clave.val()+"";
		that.Types = "DELETE";
		that.Datos = { Sml_IdPres: Clave.val(), Vch_Prese: Presentacion.val(), TipoCrud: 'Eliminar' };
		that.Ajax(token);	
	});

	$(document).on('click', '#btn_editpuro', function(e){
		e.preventDefault();
		that.Route = "/Presentacion/"+this.value+"/edit";
		that.Types = "GET";
		that.Modal(this, 2);
	});

	$(document).on('click', '#btn_deltpuro', function(e){
		e.preventDefault();
		that.Route = "/Presentacion/"+this.value+"/edit";
		that.Types = "GET";
		that.Modal(this, 3);
	});

	$(document).on('click', '.pagination a', function(e){
		e.preventDefault();
		that.page = $(this).attr('href').split('page=')[1];
		that.Route = "/Presentacion";
		that.Pagination();
	});

	$("#btn_newpres").click(function(){ that.Modal(this, 1); });

	that.CargaTabla = function(value){
		that.tabla.empty();
		that.Route = "/Presentacion";
		$.get(that.Route, function(response){
			if(value == 1) { that.CargaDatosTabla(); }
			else{ that.CargaDatosTabla(1, response); }	
		});
	}
	
	that.LlenaModal = function(response){
		$(response).each(function(){
			Clave.val(this.Sml_IdPresentacion);
			Presentacion.val(this.Vch_Presentacion);
		});
		$(that.Modall).modal('show');
	}
	
}

HoneyAppearance.prototype = HoneyPuros;

var ha = new HoneyAppearance();

$(document).ready(function(){
	ha.CargaTabla(0);
});