function HoneyTypeEnterprise(){
	HoneyPuros.apply(this, [$("#btn_creemp"), $("#btn_upemp"), $("#btn_delemp"), $('<tbody></tbody>').attr('id', 'tbody_temp'), $("#modal-tempresa"), $("#tabletemp"), 'Sml_Tipo', 'Vch_TIpo', 'tableemp' ]);
	var Clave = $("#clv_temp"), Tipo = $("#t_emp"), token = $("#token_compra").val(), that = this;

	that.btn1.click(function(){
		that.Route = "/TEmpresa/";
		that.Types = "POST";
		that.Datos = { Sml_Tipo : Clave.val(), Vch_TIpo : Tipo.val(), TipoCrud: 'Insertar' };
		that.Ajax(token);
	});

	that.btn2.click(function(){
		that.Route = "/TEmpresa/"+Clave.val()+"";
		that.Types = "PUT";
		that.Datos = { Sml_Tipo : Clave.val(), Vch_TIpo : Tipo.val(), TipoCrud: 'Actualizar' };
		that.Ajax(token);
	});

	that.btn3.click(function(){
		that.Route = "/TEmpresa/"+Clave.val()+"";
		that.Types = "DELETE";
		that.Datos = { Sml_Tipo : Clave.val(), Vch_TIpo : Tipo.val(), TipoCrud: 'Eliminar' };
		that.Ajax(token);
	});

	$(document).on('click', '.pagination a', function(e){
		e.preventDefault();
		that.page = $(this).attr('href').split('page=')[1];
		that.Route = "/TEmpresa";
		that.Pagination();
	});

	$("#btn_newtempr").click(function(){ that.Modal(this, 1) });

	$(document).on('click', '#btn_editpuro', function(e){
		e.preventDefault();
		that.Route = "/TEmpresa/"+this.value+"/edit";
		that.Types = "GET";
		that.Modal(this, 2);	
	});

	$(document).on('click', '#btn_deltpuro', function(e){
		e.preventDefault();
		that.Route = "/TEmpresa/"+this.value+"/edit";
		that.Types = "GET";
		that.Modal(this, 3);
	});

	that.CargaTabla = function(value){
		that.tabla.empty();
		that.Route = "/TEmpresa";
		$.get(that.Route, function(response){
			if(value == 1) { that.CargaDatosTabla(); }
			else{ that.CargaDatosTabla(1, response); }	
		});
	}

	that.LlenaModal = function(response){
		$(response).each(function(){
			Clave.val(this.Sml_Tipo);
			Tipo.val(this.Vch_TIpo);
		});
		$(that.Modall).modal('show');	
	}
}

HoneyTypeEnterprise.prototype = HoneyPuros;
var ht = new HoneyTypeEnterprise();

$(document).ready(function(){
	ht.Route = "/TEmpresa";
	ht.CargaDatosTabla(1);
});
