function HoneyTypePro() {
	HoneyPuros.apply(this, [$("#btn_cretipro"), $("#btn_uptipro"), $("#btn_detipro"), $('<tbody></tbody>').attr('id', 'tbody_tpro'), $("#modal-tipro"), $("#tabtipro") , 'Sml_TipoProducto', 'Vch_TipoProducto', "tabletipo"]);
	var Clave = $("#clv_tipro"), Tipo = $("#tipro"), token = $("#token_tpro").val(), that = this;

	that.btn1.click(function(){
		that.Route = "/TProducto/";
		that.Types = "POST";
		that.Datos = { Sml_IdTip: Clave.val(), Vch_Tipopro : Tipo.val(), TipoCrud : 'Insertar' };
		that.Ajax(token);
	});

	that.btn2.click(function(){
		that.Route = "/TProducto/"+Clave.val()+"";
		that.Types = "PUT";
		that.Datos = { Sml_IdTip: Clave.val(), Vch_Tipopro : Tipo.val(), TipoCrud : 'Actualizar' };
		that.Ajax(token);
	});

	that.btn3.click(function(){
		that.Route = "/TProducto/"+Clave.val()+"";
		that.Types = "DELETE";
		that.Datos = { Sml_IdTip: Clave.val(), Vch_Tipopro : Tipo.val(), TipoCrud : 'Eliminar' };
		that.Ajax(token);
	});

	$("#btn_newtpro").click(function(){ that.Modal(this, 1); });

	$(document).on('click', '#btn_editpuro', function(e){
		e.preventDefault();
		that.Route = "/TProducto/"+this.value+"/edit";
		that.Types = "GET";
		that.Modal(this, 2);
	});

	$(document).on('click', '#btn_deltpuro', function(e){
		e.preventDefault();
		that.Route = "/TProducto/"+this.value+"/edit";
		that.Types = "GET";
		that.Modal(this, 3);
	});

	$(document).on('click', '.pagination a', function(e){
		e.preventDefault();
		that.page = $(this).attr('href').split('page=')[1];
		that.Route = "/TProducto";
		that.Pagination();
	});

	that.CargaTabla = function(value){
		that.tabla.empty();
		that.Route = "/TProducto";
		$.get(that.Route, function(response){
			if(value == 1){ that.CargaDatosTabla(); }else{ that.CargaDatosTabla(1, response); }
		});
	}

	that.LlenaModal = function(response){
		$(response).each(function(){
			Clave.val(this.Sml_TipoProducto);
			Tipo.val(this.Vch_TipoProducto);
		});
		$(that.Modall).modal('show');
	}		
}

HoneyTypePro.prototype = HoneyPuros;

var ht = new HoneyTypePro();

$(document).ready(function(){
	ht.CargaTabla(0);
});